import styled from "styled-components/native";

export const Card = styled.View`
  flex: 1;
  display: flex;
  margin: ${scaleSize(20)}px ${scaleSize(10)}px;
  background-color: ${(props) => props.theme.cardBackground};
  align-self: auto;
  height: ${(props) =>
    props.type === "subCategory" ? scaleSize(200) : scaleSize(300)}px;
  width: ${(props) =>
    props.type === "subCategory" ? scaleSize(200) : scaleSize(300)}px;
  border-radius: ${scaleSize(15)}px;
  box-shadow: ${scaleSize(5)}px ${scaleSize(5)}px ${scaleSize(10)}px
    ${(props) => props.theme.shadowEffect};
`;

