`;

export default function GraphqlTest() {
  const { loading, error, data } = useQuery(FETCH_BOOKS);

  if (loading) return <Text>Loading...</Text>;
  if (error) return <Text>Error :(</Text>;

  return (
    <View>
      <FlatList
        data={data.queryBook}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
