import React from "react";
import Button from "../../components/Button";
import { resume } from "../../JSON/resume";
// import GraphqlTest from "../../components/GraphqlTest";
// import Test from "../../components/Test";
import { RootSafeAreaView } from "../../styles/RootView";
import { gql, useMutation } from "@apollo/client";

const ADD_USER = gql`
  mutation addUser($userName: String!, $resumeData: String) {
    addUser(input: { userName: $userName, resumeData: $resumeData }) {
      userName
      resumeData
    }
  }
`;

// const ADD_QUESTION = gql`
//   mutation addQuestion($question: AddQuestionInput!) {
//     addQuestion(input: $question) {
//       text
//     }
//   }
// `;

export default function HomeScreen() {
  const [addUser] = useMutation(ADD_USER);

  const upload = async () => {
    // await addUser();
    await addUser({ variables: { userName: "abc", resumeData: "res" } });
  };

  return (
    <RootSafeAreaView>
      <Button text="UPLOAD" onPress={() => upload()} />
      {/* <Test /> */}
      {/* <GraphqlTest /> */}
    </RootSafeAreaView>
  );
}
